/**
* A container class for storing Token objects.
*
* @memberof module:libresignage/markup
*/
class TokenStore {
	/**
	* Create a new TokenStore.
	*/
	constructor() {
		this.tokens = {};
	}

	/**
	* Add a Token.
	*
	* @param {Token} token The Token to add.
	*/
	add(token) {
		this.tokens[token.get_type()] = token;
	}

	/**
	* Get a token by name.
	*
	* @param {string} name The name of the Token to get.
	*/
	get(name) {
		return this.tokens[name];
	}

	/**
	* Returns the tokens in a TokenStore as an array.
	*
	* @return {Token[]} An array of Tokens.
	*/
	to_array() {
		return Object.values(this.tokens);
	}
}
module.exports = TokenStore;
