var LexerTransformation = require('libresignage/markup/Lexer/LexerTransformation');

/**
* Subclass of LexerTransformation for suppressing a certain type of Lexeme.
*
* @memberof module:libresignage/markup
*/
class SuppressTransformation extends LexerTransformation {
	/**
	* Construct a new SuppressTransformation.
	*
	* @see {@link module:libresignage/markup/Lexer/LexerTransformation.constructor}
	*/
	constructor(type) {
		super(type);
	}

	/**
	* @see {@link module:libresignage/markup/Lexer/LexerTransformation.transform}
	*/
	transform(lexemes) {
		let ret = [];
		for (let l of lexemes) {
			if (l.get_token().get_type() != this.type) { ret.push(l); }
		}
		return ret;
	}
}
module.exports = SuppressTransformation;
