var Parser = require('libresignage/markup/Markup/Parser');
var Evaluator = require('libresignage/markup/Markup/Evaluator');

/**
* Main class for markup transpilation.
*
* @memberof module:libresignage/markup
*/
class Markup {
	/**
	* Transpile markup string into HTML.
	*
	* @param {string} str The input markup.
	*
	* @return {string} The output HTML string.
	*/
	transpile(str) {
		return Evaluator.eval(Parser.parse(str));
	}
}

module.exports = Markup;
