/*
*  LibreSignage API interface entry point.
*/

exports.APIInterface = require('./api.js').APIInterface;
exports.APIEndpoints = require('./endpoints.js').APIEndpoints;
exports.APIMime      = require('./endpoints.js').APIMime;
exports.APIError     = require('./error.js').APIError;

// Add the various API classes to window.* for debugging.
if (typeof window !== 'undefined') {
	window.APIInterface = exports.APIInterface;
	window.APIEndpoints = exports.APIEndpoints;
	window.APIMime      = exports.APIMime;
	window.APIError     = exports.APIError;
}
