/*
*  A Quota object for easily checking and handling user quotas.
*/

class Quota {
	constructor(data) {
		this.data = data;
	}

	has_quota(key, amount = 1) {
		/*
		*  Check whether there's quota left for a specific action.
		*/
		return this.data[key]['limit'] - this.data[key]['used'] >= amount;
	}

	get_keys()           { return Object.keys(this.data);        }
	get_limit(key)       { return this.data[key]['limit'];       }
	get_used(key)        { return this.data[key]['used'];        }
	get_description(key) { return this.data[key]['description']; }
}
exports.Quota = Quota;
